import React from 'react'

export default function VotingBooth(props) {

    const choices = props.choices.map((option, index) => (
        <button key={option} onClick={() => props.onSelect(index)}>
        {option}
      </button>
    ))


    return (
        <div className="choices">
            { choices }
        </div>
    )
}
